// HELPER FUNCTIONS
// ==============================

// Load Districts into Dropdown
function loadDistricts() {
  const districtSelect = document.getElementById('custDistrict');
  if (!districtSelect) return;

  const districts = Object.keys(bangladeshLocations).sort();
  districts.forEach(district => {
    const option = document.createElement('option');
    option.value = district;
    option.textContent = district;
    districtSelect.appendChild(option);
  });
}

// Load Thanas based on Selected District
function loadThanas() {
  const districtSelect = document.getElementById('custDistrict');
  const thanaSelect = document.getElementById('custThana');
  const unionSelect = document.getElementById('custUnion');
  
  if (!districtSelect || !thanaSelect || !unionSelect) return;

  const selectedDistrict = districtSelect.value;

  // Reset Thana and Union
  thanaSelect.innerHTML = '<option value="">উপজেলা/থানা নির্বাচন করুন</option>';
  unionSelect.innerHTML = '<option value="">প্রথমে উপজেলা নির্বাচন করুন</option>';
  unionSelect.disabled = true;

  if (selectedDistrict && bangladeshLocations[selectedDistrict]) {
    const upazilas = Object.keys(bangladeshLocations[selectedDistrict]).sort();
    upazilas.forEach(upazila => {
      const option = document.createElement('option');
      option.value = upazila;
      option.textContent = upazila;
      thanaSelect.appendChild(option);
    });
    thanaSelect.disabled = false;
  }
}

// Load Unions based on Selected Thana
function loadUnions() {
  const districtSelect = document.getElementById('custDistrict');
  const thanaSelect = document.getElementById('custThana');
  const unionSelect = document.getElementById('custUnion');
  
  if (!districtSelect || !thanaSelect || !unionSelect) return;

  const selectedDistrict = districtSelect.value;
  const selectedThana = thanaSelect.value;

  unionSelect.innerHTML = '<option value="">ইউনিয়ন/এলাকা নির্বাচন করুন</option>';

  if (selectedDistrict && selectedThana && bangladeshLocations[selectedDistrict]) {
    const upazilaData = bangladeshLocations[selectedDistrict][selectedThana];
    if (upazilaData && Array.isArray(upazilaData)) {
      upazilaData.forEach(union => {
        const option = document.createElement('option');
        option.value = union;
        option.textContent = union;
        unionSelect.appendChild(option);
      });
      unionSelect.disabled = false;
    }
  }
}

// Initialize on Page Load
document.addEventListener('DOMContentLoaded', () => {
  loadDistricts();
});